mapper.select:
SELECT `quiz_id`, `class_id`, `user_id`, `course_id`, `date`, `quiz_detail_id`
FROM quiz


        user_id = #{userId}


        AND course_id = #{courseId}

SELECT question
FROM quiz_question
WHERE quiz_detail_id = #{quizDetailId}

SELECT quiz_answer
FROM quiz_answer
WHERE quiz_id = #{quizId}
