mapper.update:
UPDATE notice
class_Id= #{classId}, user_id = #{userId}, course_id = #{courseId}, date = #{date}, notice_detail_id = #{noticeDetailId},
WHERE class_id = #{classId} AND notice_detail_id = #{noticeDetailId}

UPDATE notice_detail
content= #{content}, title= #{title},
WHERE notice_detail_id = #{noticeDetailId}
